postMsg = function ( in_data ){
	var self = this;
	this.config={
		'cb':function(d){}
	}
	if( arguments.length>1 ) {
		 $.extend( self.config, arguments[1]);
	}
	var data={'cname':'','content':''};
	$.extend( data ,  in_data);
	if( data.cname =='' || data.content==''  ){
		throw "参数错误 pigaiPost";
		return ;
	}
	if( typeof  data.content == 'object'){
		try{
		 data.content = JSON.stringify( data.content ) ;//encodeURIComponent( JSON.stringify( data.content ));
		}catch(e){
			alert("Pleasd load json2.js");
		}
	}
	this.msg= function( str ){
		try{ 
			console.log('[pigaiPost] '+ str );
		}catch(e){}
	}
	 
	$.ajax({ url: "/icomet/push"
		,'dataType':'json'
		,'data':data
		,type: "get"
		//,success: function( data){		}
		,success: self.config.cb
		,error:function(){
			self.msg("发生错误！");
		}
	});	
}
requestChat= function( rid, txt){
	//var  content= {'uid':0,uname:'系统','img':'http://pic.pigai.org/upload/head/20150105/17.jpg','txt':'欢迎进入课堂动态模式。。。','time':'12:10'};
	var content = {'uid':0,uname:'匿名', 'txt':'' };
	if( arguments.length>2 ) {
		 $.extend(data, arguments[2]);
	}
	if( content.uid==0){
		try{
			content.uid= J.config.userId;
			content.uname= J.config.nickName;
		}catch(e){
		}
	}
	content.txt= txt;
	var data={ cname:'chat_'+rid,'content':content };
	postMsg( data );

}